mapper.select:
select comments.id,comments.comment,comments.createDate,nickname from comments,users
 where articleId=#{articleId} and comments.userId=users.id
order by createDate desc limit #{currPage},#{pageCount}

SELECT xx.id,title,createDate,updateDate,userId,visitors,fileType,targetFilePath,sourceFilePath,description,imagePath,commNum,categoryName FROM (SELECT *,CASE WHEN num IS NULL THEN 0 ELSE num END AS commNum FROM articles LEFT JOIN (SELECT COUNT(*)AS num,articleId FROM comments GROUP BY articleId) AS comments_count ON comments_count. articleId= articles.id
articles.categoryId=#{categoryId}
ORDER BY updatedate DESC LIMIT #{currPage},#{pageNum}) AS xx,category WHERE xx.categoryId=category.id;
